SELECT COUNT(DISTINCT O_car_names.Model), COUNT(DISTINCT O_model_list.Model) FROM car_names O_car_names JOIN model_list O_model_list ON O_model_list.Model=O_car_names.Model WHERE ((O_model_list.Model = "amc" AND O_model_list.Maker != 10.0) OR (SELECT COUNT(I_car_names.Model) FROM car_names I_car_names JOIN cars_data I_cars_data ON I_car_names.MakeId=I_cars_data.Id WHERE (O_car_names.MakeId = I_car_names.MakeId AND (I_car_names.Model != "'subaru'" OR I_cars_data.MPG > 30.9)) ORDER BY model_list.Maker LIMIT 4) != 3)
SELECT SUM(O_cars_data.MPG) FROM car_makers O_car_makers JOIN car_names O_car_names JOIN cars_data O_cars_data JOIN countries O_countries JOIN model_list O_model_list ON O_car_makers.Id=O_model_list.Maker AND O_car_names.MakeId=O_cars_data.Id AND O_countries.CountryId=O_car_makers.Country AND O_model_list.Model=O_car_names.Model WHERE ((O_countries.Continent = 3.0 OR O_model_list.Maker = 8.0) AND (SELECT cars_data.Year, MAX(I_cars_data.Cylinders) FROM car_names I_car_names JOIN cars_data I_cars_data JOIN model_list I_model_list ON I_car_names.MakeId=I_cars_data.Id AND I_model_list.Model=I_car_names.Model WHERE (O_car_names.Make = I_car_names.Make AND I_model_list.ModelId >= 11.0) GROUP BY cars_data.Year HAVING (COUNT(car_names.Model) >= 1 OR (MIN(cars_data.Edispl) <= 199.0 AND SUM(model_list.Maker) >= 1.0)) ORDER BY model_list.Model LIMIT 1) > 4.0)
SELECT car_makers.Country, AVG(O_model_list.ModelId) FROM car_makers O_car_makers JOIN countries O_countries JOIN model_list O_model_list ON O_car_makers.Id=O_model_list.Maker AND O_countries.CountryId=O_car_makers.Country WHERE O_countries.CountryId = (SELECT car_makers.Country, AVG(I_countries.CountryId) FROM car_makers I_car_makers JOIN countries I_countries JOIN model_list I_model_list ON I_car_makers.Id=I_model_list.Maker AND I_countries.CountryId=I_car_makers.Country WHERE (O_countries.CountryName = I_countries.CountryName AND (I_model_list.Model LIKE "%ford%" OR (I_countries.CountryName != "'japan'" OR I_car_makers.Country != 1.0))) GROUP BY car_makers.Country HAVING (MIN(model_list.ModelId) = 13.0 OR (COUNT(car_names.Model) = 2 OR MAX(car_names.MakeId) = 374.0))) GROUP BY car_makers.Country
SELECT * FROM countries O_countries WHERE (O_countries.CountryId != 2.0 OR O_countries.CountryId != (SELECT countries.CountryName, AVG(I_countries.CountryId) FROM countries I_countries WHERE (O_countries.CountryName = I_countries.CountryName AND I_countries.CountryName NOT IN "('sweden','germany','egypt','mexico')") GROUP BY countries.CountryName ORDER BY countries.CountryName LIMIT 2))
SELECT countries.CountryId, MIN(O_cars_data.Cylinders) FROM cars_data O_cars_data WHERE (SELECT cars_data.Year, SUM(I_model_list.Maker) FROM car_names I_car_names JOIN cars_data I_cars_data JOIN model_list I_model_list ON I_car_names.MakeId=I_cars_data.Id AND I_model_list.Model=I_car_names.Model WHERE (O_cars_data.Accelerate = I_cars_data.Accelerate AND I_cars_data.Horsepower >= 70.0) GROUP BY cars_data.Year HAVING (cars_data.Year > 1976.0 OR (COUNT(model_list.Model) > 3 OR COUNT(car_names.Make) >= 3)) ORDER BY cars_data.Weight LIMIT 2) <= 524.0 GROUP BY countries.CountryId HAVING ((COUNT(car_makers.Maker) <= 1 OR SUM(cars_data.Accelerate) != 11.6) AND COUNT(car_makers.FullName) > 2) ORDER BY car_makers.Id
SELECT car_makers.Country, COUNT(DISTINCT O_model_list.Model) FROM model_list O_model_list WHERE (SELECT MIN(I_car_makers.Id) FROM car_makers I_car_makers WHERE (O_car_makers.Maker = I_car_makers.Maker AND I_car_makers.FullName NOT LIKE "%Toyota%") ORDER BY car_makers.FullName LIMIT 1) = 1.0 GROUP BY car_makers.Country ORDER BY car_makers.Country LIMIT 2
SELECT model_list.Maker, COUNT(DISTINCT O_model_list.Model), COUNT(DISTINCT O_countries.CountryName) FROM car_makers O_car_makers JOIN countries O_countries JOIN model_list O_model_list ON O_car_makers.Id=O_model_list.Maker AND O_countries.CountryId=O_car_makers.Country WHERE ((SELECT AVG(I_car_makers.Country) FROM car_makers I_car_makers JOIN countries I_countries ON I_countries.CountryId=I_car_makers.Country WHERE (O_model_list.ModelId = I_model_list.ModelId AND I_countries.Continent = 1.0)) <= 1.0 OR O_model_list.ModelId >= 34.0) GROUP BY model_list.Maker
SELECT O_cars_data.MPG FROM cars_data O_cars_data WHERE ((SELECT MAX(I_cars_data.Cylinders) FROM car_names I_car_names JOIN cars_data I_cars_data JOIN model_list I_model_list ON I_car_names.MakeId=I_cars_data.Id AND I_model_list.Model=I_car_names.Model WHERE (O_cars_data.Id = I_cars_data.Id AND (I_car_names.MakeId != 270.0 OR (I_model_list.ModelId = 1.0 OR I_model_list.Maker = 9.0))) ORDER BY model_list.ModelId) = 8.0 AND O_cars_data.Edispl > 400.0)
SELECT countries.CountryId, COUNT(DISTINCT O_continents.Continent), COUNT(DISTINCT O_countries.CountryName) FROM continents O_continents JOIN countries O_countries ON O_continents.ContId=O_countries.Continent WHERE (SELECT COUNT(I_continents.Continent) FROM continents I_continents JOIN countries I_countries ON I_continents.ContId=I_countries.Continent WHERE (O_countries.Continent = I_countries.Continent AND (I_countries.CountryId != 1.0 AND I_continents.ContId != 2.0))) = 4 GROUP BY countries.CountryId ORDER BY countries.CountryId
SELECT car_makers.Maker, COUNT(DISTINCT O_car_makers.Maker) FROM car_makers O_car_makers WHERE (SELECT AVG(I_car_makers.Id) FROM car_makers I_car_makers JOIN countries I_countries ON I_countries.CountryId=I_car_makers.Country WHERE (O_car_makers.Maker = I_car_makers.Maker AND (I_countries.CountryId != 2.0 AND I_car_makers.FullName != "'Subaru'")) ORDER BY cars_data.Accelerate LIMIT 3) = 1.0 GROUP BY car_makers.Maker HAVING ((AVG(car_makers.Country) = 1.0 AND COUNT(car_makers.Country) <= 4) OR COUNT(car_makers.Country) > 4)
